  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/abhishe/OneDrive/Desktop/Indori-Artist/artist-backend/node_modules/semver/bin/node_modules:/mnt/c/Users/abhishe/OneDrive/Desktop/Indori-Artist/artist-backend/node_modules/semver/node_modules:/mnt/c/Users/abhishe/OneDrive/Desktop/Indori-Artist/artist-backend/node_modules:/mnt/c/Users/abhishe/OneDrive/Desktop/Indori-Artist/node_modules:/mnt/c/Users/abhishe/OneDrive/Desktop/node_modules:/mnt/c/Users/abhishe/OneDrive/node_modules:/mnt/c/Users/abhishe/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/abhishe/OneDrive/Desktop/Indori-Artist/artist-backend/node_modules/.pnpm/node_modules"
